import HomeTitleBox from "./homeComponents/HomeTitleBox";
import HomeItemTable from "./homeComponents/HomeItemTable";
import { useState, useEffect } from 'react'
import axios from "axios";

function Home() {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(1);
  const [lastMes, setLastMes] = useState({
    mes : '',
    index : 1
  });

  const getItems = (start) => {
    axios.get('http://localhost:3001/merchdisplay', {
      params: {
        start : start,
      }
    }).then((response) => {
      setItems(response.data);
      setLastMes({
        mes : 'http://localhost:3001/merchdisplay',
        index : response.data[response.data.length - 1].id
      });
    });
  };

  useEffect(() => {
    getItems(lastMes.index);
  },[]);

  return (
    <div>
      <HomeTitleBox setItems={setItems}/>
      <HomeItemTable items={items} page={page} setPage={setPage}/>
    </div>
  );
}

export default Home;
